import React, { useState, useEffect } from 'react';

const TestUseEffect = () => {
    const [count, setCount] = useState(180);
    const handleCount = () => {
        if(count > 0){
            setTimeout(() =>{
                setCount(count - 5)
            }, 5000);
        }

    }
    useEffect(handleCount,[])

    return (
        <div>
            <h2>{count}</h2>
            <button onClick={handleCount}>Down</button>                   
        </div>
    );
};

export default TestUseEffect;
