        setCount(count + 1);

        setCount(count + 1);
  
        // setCount(count => count + 1);
        // setCount(count => count + 1);
    }

    return (
        <div  className="counter">
            <button onClick={handleDoubleCounter}>Double</button>
            <div>{count}</div>
        </div>
    );
};

export default DoubleCounter;
