import React, { useEffect, useState } from 'react';
import axios from 'axios';

const getPhotos = () => {
    return axios.get('https://picsum.photos/v2/list?limit=10')
        .then(response => {
            // console.log(response);
            // tra ve mot promise
            // console.log(response.data);
            return response.data;
        })
        .catch(err => {
            console.log(err);
        });
};

const Photos = () => {
    const [randomPhotos, setRandomPhotos] = useState([]);
    // console.log("da render");
    useEffect(() => {
        
        getPhotos().then((images) => {
            setRandomPhotos(images);
        });
        // console.log("da useEffect2");
    },[]);
    return (
        <div>

            <div className="grid grid-cols-5 gap-5 p-5">
                {randomPhotos.map((image,index)=>{
                    return (
                        <div key={image.id} className="p-3 bg-white shadow-md rounded-lg h-[200px]">
                            <img
                                src = {image.download_url}
                                alt = {image.author}
                                className="w-full h-full object-cover rounded-lg"

                            />
                        </div>
                    )
                })}
            </div>
            <button className="inline-block px-6 py-3 bg-purple-500 text-white">Load More</button>
        </div>
    );
};

export default Photos;
