import React, { useEffect, useRef, useState } from 'react';

const DropDown = () => {
    const [showDropDown, setShowDropDown] = useState(false);
    const dropDownRef = useRef();
    const handleClickOutDropDown = (e) => {
        if(dropDownRef.current && !dropDownRef.current.contains(e.target)){
            setShowDropDown(false);
        }
    }
    useEffect(() => {
        document.addEventListener('click', handleClickOutDropDown);
        return () => {
            document.removeEventListener('click', handleClickOutDropDown);
        }
    }, []);
    return (
        <div className="relative max-w-[400px]" ref={dropDownRef}>
            <div className="p-5 border border-gray-200 rounded-lg w-full cursor-pointer"
                onClick={() => setShowDropDown(!showDropDown)}
            >
                Selected
            </div>
            {showDropDown && <div className="border border-gray-200 rounded-lg absolute top-full left-0 w-full" >
                <div className="p-5 cursor-pointer border-b border-gray-200 ">Javascript</div>
                <div className="p-5 cursor-pointer border-b border-gray-200 ">React</div>
                <div className="p-5 cursor-pointer border-b border-gray-200 ">html</div>
                <div className="p-5 cursor-pointer border-b border-gray-200 ">css</div>
            </div>}
        </div>
    );
};

export default DropDown;
