import React, { useState, useEffect } from 'react';

const TestUseEffect = () => {
    const [count, setCount] = useState(180);
  
    // useEffect(() => { 
    //     if(count > 0){
    //         const time =setTimeout(() =>{
    //             setCount(count - 1)
    //         }, 1000);
    //     }
    // },[count])
    if(count > 0){
        setTimeout(() =>{
            setCount(count - 1)
        }, 1000);
    }

    return (
        <div>
            <h2 className="text-center text-5xl">{count}</h2>
                           
        </div>
    );
};

export default TestUseEffect;
