import React from 'react';
import styled from 'styled-components'

const CardStyled = styled.div`
    position: relative;
    margin-top: 100px;
`;
const CardImageWrapper = styled.div`
    height: 400px;
    width: 400px;
    border-radius: 10px;
`;
const CardImage = styled.img`
    display: block;
    object-fit: cover;
    width: 100%;
    height: 100%;
    border-radius: inherit;
`;
const CardContent = styled.div`
    position: absolute;
    background-color: #ccc;
    left: 50%;
    transform: translate(-50%, 0);
    bottom: 0;
    z-index: 2;
    border-radius: 20px;
    padding: 20px;
`;
const CardTop = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    margin-bottom: 30px;
`;
const CardUser = styled.div`
    display: flex;
    align-items: center;
    column-gap: 12px;
`;
const CardUserImage = styled.img`
    width: 30px;
    height: 30px;
    border-radius: 30px;
    object-fit: cover;
`;
const Card = () => {
    return (
        <CardStyled>
            <CardImageWrapper>
                <CardImage src="https://cdn.dribbble.com/users/2400293/screenshots/18034200/media/7c9ad36bd345b48cdb1a1db87ba5d096.png?compress=1&resize=1200x900&vertical=top" alt="" />
            </CardImageWrapper>
            <CardContent>
                <CardTop>
                    <CardUser>
                        <CardUserImage src="https://images.unsplash.com/photo-1650827788529-d12062e0351f?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
                        alt="" />
                        <span>@hieulinux</span>
                    </CardUser>
                    <div>
                        <span>256</span>
                    </div>
                </CardTop>
                <div>
                   <h3>Cosmic Perspective</h3>
                   <span>12000 vnd</span>
                </div>
            </CardContent>
           
           
        </CardStyled>
    );
};

export default Card;
